<?php/** * @link      https://github.com/demodyne/demodyne * @copyright Copyright (c) 2015-2016 Demodyne (https://www.demodyne.org) * @license   http://www.gnu.org/licenses/agpl.html GNU Affero General Public License */namespace DGIModule\Controller;use Zend\Mvc\Controller\AbstractActionController;use Zend\View\Model\ViewModel;use Zend\Session\Container;use DGIModule\Form\AddEditBannerForm;use DGIModule\Entity\Banner;use DGIModule\Entity\User;class BannerController extends AbstractActionController{    public function myBannersAction() {        $user = $this->identity();        $viewModel = new ViewModel();        // inactive banner section        $inactiveBannersSection = $this->forward()->dispatch('DGIModule\Controller\Banner', array('action' => 'inactive-banners'));        $viewModel->addChild($inactiveBannersSection, 'inactiveBannersSection');        $activeBannersSection = $this->forward()->dispatch('DGIModule\Controller\Banner', array('action' => 'active-banners'));        $viewModel->addChild($activeBannersSection, 'activeBannersSection');        $viewModel->setVariables([            'user' => $user        ]);        return $viewModel;    }        public function addBannerAction() {         $user = $this->identity();                $publish = $this->params()->fromRoute('publish', false);                $session = new Container('level');        $level = $session->level;                $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');        $bannersCount = $entityManager->getRepository('DGIModule\Entity\Banner')->countActiveBanners($user->getAdmin());        $bannersCount = $bannersCount["total"];        $banner = new Banner();        $form = new AddEditBannerForm();        $form->setAttribute('action', $this->url()->fromRoute('administration/banner', array('action'=>'add-banner')));        $request = $this->getRequest();        if ($request->isPost()) {            $post = array_merge_recursive(                $request->getPost()->toArray(),                $request->getFiles()->toArray()                );            $form->bind($banner);            $form->setData($post);                        // TODO verify file size            if ($form->isValid()) {                //verify if picture change                $picture = $post["bannerImage"]["name"];                if ($picture!="") {                    $files   = $request->getFiles();                    $target = getcwd() . "/public/img/banners/banner.jpg";                    $filterR = new \Zend\Filter\File\Rename(array(                        "target"    => $target,                        "randomize" => true,                    ));                    $filename= $filterR->filter($files['bannerImage']);                    chmod($filename["tmp_name"], 0644);                    $banner->setBannerImage(str_replace(getcwd() . "/public", '', $filename["tmp_name"]));                }                else {                    $banner->setBannerImage(null);                }                                $banner->setAdmin($user->getAdmin())                       ->setBannerLevel($user->getAdmin()->getAdminLevel());                if ($level=='city' && $user->getAdmin()->getAdminCity()) {                    $banner->setCity($user->getAdmin()->getAdminCity());                }                else {                    $banner->setCity($user->getCity());                }                if ($publish) {                    $banner->setBannerPublished(1)                           ->setBannerPosition(count($user->getAdmin()->getActiveBanners()) + 1);                }                $entityManager->persist($banner);                $entityManager->flush();                return new \Zend\View\Model\JsonModel(array('success' => true));            }        }        $viewModel = new ViewModel();        //disable layout if request by Ajax        $viewModel->setTerminal($this->getRequest()->isXmlHttpRequest());        $viewModel->setVariables([            'form'=>$form,            'user' => $user,            'bannersCount' => $bannersCount        ]);	    return $viewModel;	}	public function editBannerAction() {	    $user = $this->identity();	    $publish = $this->params()->fromRoute('publish', false);	    $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	    $bannersCount = $entityManager->getRepository('DGIModule\Entity\Banner')->countActiveBanners($user->getAdmin());	    $bannersCount = $bannersCount["total"];	    $bannerUUID = $this->params('id');	    // no parameter	    if (!$bannerUUID)	        return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied'));        $banner = $entityManager->getRepository('DGIModule\Entity\Banner')->findOneBy(array('bannerUUID' => $bannerUUID));        // proposal doesn't exists or the logged user is not the owner of the scenario        if (!$banner || !$user->isAdministration() || $banner->getAdmin()!=$user->getAdmin())            return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied'));	    $form = new AddEditBannerForm();	    $form->setAttribute('action', $this->url()->fromRoute('administration/banner', array('action'=>'edit-banner', 'id'=>$bannerUUID)));	    $form->get('bannerName')->setValue($banner->getBannerName());	    $form->get('bannerDescription')->setValue($banner->getBannerDescription());	    $form->get('bannerUrl')->setValue($banner->getBannerUrl());	    $oldFilename = $banner->getBannerImage();	    $request = $this->getRequest();	    if ($request->isPost()) {	        $post = array_merge_recursive(	            $request->getPost()->toArray(),	            $request->getFiles()->toArray()	            );	        $form->bind($banner);	        $form->setData($post);	        //verify if picture change	        $picture = $post["bannerImage"]["name"];	        if ($picture == "") { // if no new image                $form->getInputFilter()->remove('bannerImage');            }	        if ($form->isValid()) {	            if ($picture!="") {	                $files   = $request->getFiles();	                $target = getcwd() . "/public/img/banners/banner.jpg";	                $filterR = new \Zend\Filter\File\Rename(array(	                    "target"    => $target,	                    "randomize" => true,	                ));	                $filename= $filterR->filter($files['bannerImage']);	                chmod($filename["tmp_name"], 0644);	                $banner->setBannerImage(str_replace(getcwd() . "/public", '', $filename["tmp_name"]));	                if ($oldFilename && file_exists(getcwd() . '/public' . $oldFilename)) {	                    unlink(getcwd() . '/public' . $oldFilename);	                }                }                else {                    $banner->setBannerImage($oldFilename);                }                if ($publish) {                    $banner->setBannerPublished(1)                            ->setBannerPosition(count($user->getAdmin()->getActiveBanners()) + 1);                }                $entityManager->persist($banner);                $entityManager->flush();                return new \Zend\View\Model\JsonModel(array('success' => true));            }        }        $viewModel = new ViewModel();        //disable layout if request by Ajax        $viewModel->setTerminal($this->getRequest()->isXmlHttpRequest());        $viewModel->setTemplate('dgi-module/banner/add-banner.phtml');        $viewModel->setVariables([            'form'=>$form,            'user' => $user,            'banner' => $banner,            'bannersCount' => $bannersCount        ]);        return $viewModel;    }    public function deleteBannerAction()    {        $user = $this->identity();        $bannerUUID = $this->params('id');        // no parameter        if (!$bannerUUID) {            return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied', 'dialog'=>true));        }        $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');        $banner = $entityManager->getRepository('DGIModule\Entity\Banner')->findOneBy(array('bannerUUID' => $bannerUUID));        // banner not exists or user not owner        if (!$banner || $banner->getAdmin()!=$user->getAdmin()) {            return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied', 'dialog'=>true));        }        $request = $this->getRequest();        if ($request->isPost()){            // if already deleted            $oldFilename = $banner->getBannerImage();            if ($oldFilename && file_exists(getcwd() . '/public' . $oldFilename)) {                unlink(getcwd() . '/public' . $oldFilename);            }            $entityManager->remove($banner);            $entityManager->flush();            return new \Zend\View\Model\JsonModel(array('success' => true));        }        $viewmodel = new ViewModel();        //disable layout if request by Ajax        $viewmodel->setTerminal($this->getRequest()->isXmlHttpRequest());        $viewmodel->setVariables([            'banner' => $banner,        ]);        return $viewmodel;    }	public function inactiveBannersAction() {	    $user = $this->identity();	    $session = new Container('banner');	    $page = $this->params()->fromRoute('page', null);	    if (!$page) {	        if (!$session->myInactiveBannersPage) {	            $page = 1;	        }	        else {	            $page = $session->myInactiveBannersPage;	        }	    }	    $session->myInactiveBannersPage = $page;	    $sort = $this->params()->fromRoute('sort', null);	    if (!$sort) {	        if (!$session->myInactiveBannersSort) {	            $sort = 'name';	        }	        else {	            $sort = $session->myInactiveBannersSort;	        }	    }	    $session->myInactiveBannersSort = $sort;	    $order = $this->params()->fromRoute('order', null);	    if (!$order) {	        if (!$session->myInactiveBannersOrder) {	            $order = 'asc';	        }	        else {	            $order = $session->myInactiveBannersOrder;	        }	    }	    $session->myInactiveBannersOrder = $order;	    $limit= $this->params()->fromRoute('results', null);	    if (!$limit) {	        if (!$session->myInactiveBannersResults) {	            $limit = 5;	        }	        else {	            $limit = $session->myInactiveBannersResults;	        }	    }	    $session->myInactiveBannersResults = $limit;	    $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	    $bannersCount = $entityManager->getRepository('DGIModule\Entity\Banner')->countInactiveBanners($user->getAdmin());	    $bannersCount = $bannersCount["total"];	    $offset = ($page == 0) ? 0 : ($page - 1) * $limit;	    $pagedInactiveBanners = $entityManager->getRepository('DGIModule\Entity\Banner')->getAdminInactiveBanners($user->getAdmin(), $offset, $limit, $sort, $order);	    $viewModel = new ViewModel();	    $terminal = $this->getRequest()->isXmlHttpRequest();	    $viewModel->setTerminal($terminal);	    $viewModel->setVariables([	        'pagedInactiveBanners' => $pagedInactiveBanners,	        'limit' => $limit,	        'page' => $page,	        'sort' => $sort,	        'order' => $order,	        'user' => $user,	        'bannersCount' => $bannersCount,	        'terminal' => $terminal	    ]);	    return $viewModel;	}	public function activeBannersAction() {	    $user = $this->identity();        $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	    $bannersCount = $entityManager->getRepository('DGIModule\Entity\Banner')->countActiveBanners($user->getAdmin());	    $bannersCount = $bannersCount["total"];	    $activeBanners = $entityManager->getRepository('DGIModule\Entity\Banner')->getAdminActiveBanners($user->getAdmin());	    $viewModel = new ViewModel();	    $terminal = $this->getRequest()->isXmlHttpRequest();	    $viewModel->setTerminal($terminal);	    $viewModel->setVariables([	        'activeBanners' => $activeBanners,	        'user' => $user,	        'bannersCount' => $bannersCount	    ]);	    return $viewModel;	}	public function carouselBannersAction() {	    $user = $this->identity();	    	    $guestSession = new Container('guest');	    if (!$user &&  !$guestSession->country) {	        return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied'));	    }	    $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	    	    if (!$user) {	        $user = new User();	        $user->setUsrId(0);	    	    }	    else {	        $user = clone($user);	    }	    	    if ($guestSession->country) {	        $user->setCountry($entityManager->getRepository('DGIModule\Entity\Country')->findOneBy(['countryId' => $guestSession->country]));	        if ($guestSession->city) {	            $user->setCity($entityManager->getRepository('DGIModule\Entity\City')->findOneBy(['cityId' => $guestSession->city]));	        }	    }	    	    	    $session = new Container('level');	    $level = $session->level;	    	    $config = $this->getServiceLocator()->get('Config');	    	    $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	       $activeBanners = $entityManager->getRepository('DGIModule\Entity\Banner')->getActiveBanners($user, $level, $config['demodyne']['proposal']['level']);	    $viewModel = new ViewModel();	    $terminal = $this->getRequest()->isXmlHttpRequest();	    $viewModel->setTerminal($terminal);	    $viewModel->setVariables([	        'activeBanners' => $activeBanners,	        'user' => $user,	    ]);	    return $viewModel;	}	public function sortActiveBannersAction() {	    $user = $this->identity();	    $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');	    $request = $this->getRequest();	    if ($request->isPost()){	        $sortedBanners = $this->params()->fromPost('banners');	        foreach ($sortedBanners as $index => $bannerUUID) {	            $banner = $entityManager->getRepository('DGIModule\Entity\Banner')->findOneBy(array('bannerUUID' => $bannerUUID));	            $banner->setBannerPosition($index+1);	            $entityManager->merge($banner);	        }	        $entityManager->flush();	        return new \Zend\View\Model\JsonModel(array('success' => true));	    }	    return new \Zend\View\Model\JsonModel(array('success' => false));	}	public function publishBannerAction()	{	    $user = $this->identity();	    $bannerUUID = $this->params('id');	    // no parameter	    if (!$bannerUUID)	        return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied'));        $entityManager = $this->getServiceLocator()->get('doctrine.entitymanager.orm_default');        $banner = $entityManager->getRepository('DGIModule\Entity\Banner')->findOneBy(array('bannerUUID' => $bannerUUID));        // proposal doesn't exists or the logged user is not the owner of the scenario        if (!$banner || $banner->getAdmin()!=$user->getAdmin())            return $this->forward()->dispatch('DGIModule\Controller\Error', array('action' => 'access-denied'));        $request = $this->getRequest();        $added = false;        $removed = false;        if ($request->isPost()){            // if already published            if ($banner->getBannerPublished()) {                $banner->setBannerPublished(0)                       ->setBannerPosition(-1);                $removed = true;            }            else {                $banner->setBannerPublished(1)                       ->setBannerPosition(count($user->getAdmin()->getActiveBanners()) + 1);            }            $entityManager->merge($banner);            $entityManager->flush();            $this->sortActiveBanners($entityManager, $user);            return new \Zend\View\Model\JsonModel(array('added' => $added, 'removed'=>$removed));        }        $viewModel = new ViewModel();        //disable layout if request by Ajax        $viewModel->setTerminal($this->getRequest()->isXmlHttpRequest());        if ($banner->getBannerPublished()) {            $viewModel->setTemplate('dgi-module/banner/unpublish-banner.phtml');        }        $viewModel->setVariables([            'banner' => $banner,            'user' => $user        ]);        return $viewModel;	}	private function sortActiveBanners($entityManager, $user) {	    $session = new Container('level');	    $level = $session->level;	     	    $config = $this->getServiceLocator()->get('Config');	    	    $banners = $entityManager->getRepository('DGIModule\Entity\Banner')->getActiveBanners($user, $level, $config['demodyne']['proposal']['level']);	    foreach ($banners as $index => $banner) {	        $banner->setBannerPosition($index+1);	        $entityManager->merge($banner); 	    }	    $entityManager->flush();	}}