<?phpnamespace DGIModule\Controller\Factories;use DGIModule\Controller\CommentThumbController;use Zend\ServiceManager\FactoryInterface;use Zend\ServiceManager\ServiceLocatorInterface;class CommentThumbControllerFactory implements FactoryInterface{    /**     * @param ServiceLocatorInterface $serviceLocator     * @return CommentThumbController     */    public function createService(ServiceLocatorInterface $serviceLocator)    {        $sl = $serviceLocator->getServicelocator();        return new CommentThumbController(            $sl->get('doctrine.entitymanager.orm_default'));    }	}