<?phpnamespace DGIModule\Controller\Factories;use DGIModule\Controller\ReferendumController;use Zend\ServiceManager\FactoryInterface;use Zend\ServiceManager\ServiceLocatorInterface;class ReferendumControllerFactory implements FactoryInterface{    /**     * @param ServiceLocatorInterface $serviceLocator     * @return ReferendumController     */    public function createService(ServiceLocatorInterface $serviceLocator)    {        $sl = $serviceLocator->getServicelocator();        return new ReferendumController(            $sl->get('Config'),            $sl->get('doctrine.entitymanager.orm_default'),            $sl->get('MvcTranslator'),            $sl->get('Zend\Authentication\AuthenticationService'));    }	}