<?php /** * @link      https://github.com/demodyne/demodyne * @copyright Copyright (c) 2015-2016 Demodyne (https://www.demodyne.org) * @license   http://www.gnu.org/licenses/agpl.html GNU Affero General Public License */namespace DGIModule\Form;use DGIModule\Entity\Administration; use Zend\Form\Fieldset; use Zend\InputFilter\InputFilterProviderInterface; class AdministrationFieldset extends Fieldset implements InputFilterProviderInterface {     public function __construct()     {         parent::__construct('administration-fieldset');         $this             ->setObject(new Administration())         ;         $this->add(array(             'name' => 'adminName',             'options' => array(                 'label' => 'Name:',             ),             'attributes' => array(                 'required' => 'required',                 'class'=>'form-control text-change',             ),         ));         $this->add([             'type' => 'Zend\Form\Element\Textarea',             'name' => 'adminPresentation',             'attributes' => array(                 'required' => 'required',                 'rows' => 4,                 'class'=>'form-control text-change',                 'id' => 'adminPresentation'             ),             'options' => [                 'label' => 'Presentation: ',             ]         ]);         $this->add(array(             'name' => 'adminLevel',             'type'  => 'Zend\Form\Element\Select',             'options' => array(                 'label' => 'Level: ',                 'value_options' => [                     3 => "Country",                     2 => "Region",                     1 => "City"                 ],             ),             'attributes' => [                 'id' => 'adminLevel',                 'class'=>'form-control text-change',                 'required' => 'required',             ]         ));         $this->add(array(             'name' => 'adminAddress',             'options' => array(                 'label' => 'Address:',             ),             'attributes' => array(                 'required' => 'required',                 'class'=>'form-control text-change',             ),         ));         $this->add(array(             'name' => 'adminWebsite',             'type' => 'Zend\Form\Element\Url',             'options' => array(                 'label' => 'Website:',             ),             'attributes' => array(                 'required' => 'required',                 'class'=>'form-control text-change',                 'onblur' => 'checkURLInput(this)',             ),         ));         $this->add(array(             'name' => 'adminFax',             'type'  => 'DGIModule\Form\Element\Phone',             'options' => array(                 'label' => 'Fax:',             ),             'attributes' => [                 'id' => 'fax',                 'maxlength' => 12,                 'size' => 12,                 'class'=>'form-control text-change',             ]         ));         $this->add(array(             'name' => 'adminPostalcode',             'attributes' => array(                 'type'  => 'text',                 'class'=>'form-control text-change',                 'id' => 'adminPostalcode',                 'maxlength' => 5,                 'size' => 5,             ),             'options' => array(                 'label' => 'Postalcode:',             ),         ));         $this->add(array(             'name' => 'adminRegion',             'type'  => 'Zend\Form\Element\Select',             'options' => array(                 'label' => 'City: ',                 'disable_inarray_validator' => true,             ),             'attributes' => [                 'id' => 'adminRegion',                 'required' => 'required',                 'class'=>'form-control text-change',             ]         ));     }     /**      * @return array      */     public function getInputFilterSpecification()     {         return array(             'adminName' => array(                 'required' => true,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'min'      => 1,                             'max'      => 100,                         ),                     ),                 ),             ),             'adminAddress' => array(                 'required' => true,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'min'      => 1,                             'max'      => 100,                         ),                     ),                 ),             ),             'adminPresentation' => array(                 'required' => true,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'min'      => 1,                             'max'      => 1000,                         ),                     ),                 ),             ),             'adminWebsite' => array(                 'required' => true,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'max'      => 500,                         ),                     ),                 ),             ),             'adminPostalcode' => array(                 'required' => false,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'max'      => 20,                         ),                     ),                 ),             ),             'adminCity' => array(                 'required' => false,             ),             'adminFax' => array(                 'required' => false,                 'filters'  => array(                     array('name' => 'StripTags'),                     array('name' => 'StringTrim'),                 ),                 'validators' => array(                     array(                         'name'    => 'StringLength',                         'options' => array(                             'encoding' => 'UTF-8',                             'min'      => 10,                             'max'      => 12,                         ),                     ),                 ),             ),         );     } } 